    /**
     * Tests serializing and parsing the the same VM.
     * @throws Exception
     */
    public void testSerializeAndParse() throws Exception {

        Certificate cert = CertTools.getCertfromByteArray(Base64.decode(CERT_XMLSIGNER.getBytes()));
        assertNotNull(cert);

        final Validation validation1 = new Validation(cert,
                Collections.singletonList(cert), Status.VALID, "Okey message");
        final ByteArrayOutputStream bout = new ByteArrayOutputStream();
        validation1.serialize(new DataOutputStream(bout));

        final byte[] b = bout.toByteArray();
        
//        for(int i = 0; i  < b.length; i++) {
//            System.out.print("(byte) " + b[i] + ", ");
//        }

        final Validation validation2 = new Validation();
        validation2.parse(new DataInputStream(new ByteArrayInputStream(b)));
        assertEquals(validation1.getStatusMessage(), validation2.getStatusMessage());
        assertEquals(validation1.getValidationDate(), validation2.getValidationDate());
        assertEquals(validation1.getStatus(), validation2.getStatus());
        assertEquals(validation1.getCertificate(), validation2.getCertificate());
        assertEquals(validation1.getCAChain().size(), validation2.getCAChain().size());
    }

    /**
     * Tests parsing of previously serialized data.
     * @throws Exception
     */
    public void testParseValidation1() throws Exception {
        final Validation validation1 = new Validation();
        validation1.parse(new DataInputStream(new ByteArrayInputStream(
                VALIDATION1_JAVA2)));
        assertEquals("Okey message", validation1.getStatusMessage());
        assertNotNull(validation1.getValidationDate());
        assertEquals(Status.VALID, validation1.getStatus());
        assertNotNull(validation1.getCertificate());
        assertEquals(1, validation1.getCAChain().size());
    }


